import React, {useState} from 'react';
import { Form, ButtonToolbar, Button, FlexboxGrid, Panel, Animation } from 'rsuite';



function Login() {
    const [Reg_Log, setReg_Log] = useState("Bejelentkezés")
    
    const Flexboxgrid = React.forwardRef((props, ref) => (
        <div
        {...props}
        ref={ref}
        //style={{ background: '#000', width: 100, height: 160, overflow: 'hidden' }}
        >
            <FlexboxGrid justify="center" style={{ marginTop: '40px' }}>
                <Panel header={<h3 style={{ textAlign: 'center' }}>{Reg_Log}</h3>} bordered>
                    <Form>
                        <Form.Group>
                            <Form.ControlLabel hidden={Reg_Log !== "Regisztrálás"}>Teljes Név</Form.ControlLabel>
                            <Form.Control name="name" />
                            <Form.ControlLabel>Email</Form.ControlLabel>
                            <Form.Control name="email" type="email" />
                            <Form.ControlLabel hidden={Reg_Log === "Regisztrálás"}>Telefonszám</Form.ControlLabel>
                            <Form.Control name="phone" />
                            <Form.HelpText hidden={Reg_Log === "Regisztrálás"}>pl.: +36301234567</Form.HelpText>
                            <Form.ControlLabel>Jelszó</Form.ControlLabel>
                            <Form.Control name="password" type="password" autoComplete="off" />
                        </Form.Group>
                        <Form.Group>
                        <ButtonToolbar>
                            <Button appearance="primary" onClick={() => setReg_Log("Regisztráció")}>Regisztrálás</Button>
                            <Button appearance="default" onClick={() => setReg_Log("Bejelentkezés")}>Bejelentkezés</Button>
                        </ButtonToolbar>
                        </Form.Group>
                    </Form>
                </Panel>
            </FlexboxGrid>
        </div>
    ));

  function handleReg() {
    setReg_Log("Regisztráció");
  }

  function handleLog() {
    setReg_Log("Bejelentkezés");
  }
  return (
    <div>
      <Animation.Bounce in={true}>
        {(props, ref) => <Flexboxgrid {...props} ref={ref} />}
      </Animation.Bounce>
    </div>
  );
}

export default Login;
