import React, { useState, useEffect } from 'react'
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid';
import huLocale from '@fullcalendar/core/locales/hu';
import interactionPlugin, { Draggable } from '@fullcalendar/interaction'
import { createEventId } from './event-utils'; // Import createEventId function
import 'rsuite/dist/rsuite.min.css';
import '../Custom.css';
import { Button, CustomProvider, Modal, Container, Grid, Col, Form, InputNumber, Nav, Navbar, SelectPicker, DatePicker  } from 'rsuite';
//Navbar-ba beépíteni a behúzható eseményeket -->ChatGPT

//DatePicker -->Nem heti ismétlődő események -
//-> renderEvents
//Overlapping eventek kezelése?
//https://rsuitejs.com/guide/introduction/#license

//Form -->Plaintext - input váltás (példa alapján)

export default function DemoApp() {
  const [open, setOpen] = useState(false);
  const [editable, setEditable] = useState(true);
  const [showEventData, setshowEventData] = useState([]);
  const [showExtendedData, setshowExtendedData] = useState([]);
  const [buttonTexts, setButtonTexts] = useState(["Létrehozás", "Esemény Elvetése"]);
  const timelength = ["1", "1.5", "2"].map(item => ({label: item, value: item}));
  const [numDatePickers, setnumDatePickers] = useState(1);
  const handleOpen = openType => {
    setEditable(openType);
    setOpen(true);
  };
  const handleClose = () => setOpen(false);


  useEffect(() => {
    const containerEl = document.querySelector("#events");
    let draggable = new Draggable(containerEl, {
      itemSelector: ".event",
      eventData: (eventEl) => {
        const duration = parseFloat(eventEl.getAttribute('data-duration')); // Get duration from data attribute
        const end = calculateEndTime(duration);
        const platform = eventEl.getAttribute('platform');
        if (duration === 0.5){
          var corrected = "00:30";
        }
        else if (duration === 1.5){
          var corrected = "01:30";
        }
        else{
          var corrected = duration + ":00";
        }
        return {
          id: createEventId(),
          backgroundColor: "green",//#2EFF2E, lightgreen
          textColor: "white",//Black
          title: eventEl.innerText,
          platform : platform,
          duration: corrected,
          start: new Date(),
          location: "",
          occasions: 1,
          traintitle: "Tréning címe/témája",
          lengths: duration,
          end: end 
        };
      }
    });
    return () => draggable.destroy();
  }, []);

  //const [currentEvents, setCurrentEvents] = useState([])
  const INITIAL_EVENTS = [
    {
      title: "Esemény 0",
      platform: "Online",
      duration: "02:00",
      date: new Date()
    }
  ];

  function handleEventClick(clickInfo) {
      console.log(clickInfo.event.start);
      setshowExtendedData(clickInfo.event.extendedProps);
      setshowEventData(clickInfo.event);
      setButtonTexts(["Szerkesztés", "Kilépés"]);//Jogosultsáogtól függően más
      handleOpen(true);
  }

  function eventReceive(info){
    //window.alert("Időzítésileg itt lenne az extra datok megadása...");
    const helyszín = "Pesti ház";
    info.event.setExtendedProp("location", helyszín);
    setshowExtendedData(info.event.extendedProps);
    setshowEventData(info.event);
    setButtonTexts(["Létrehozás", "Esemény Elvetése"]);
    handleOpen(false);
    return {};
  };

  const createDates = (value) => {
    setnumDatePickers(value);
  };
  

  return (
    <CustomProvider>
      <Navbar className='centered-nav navbar-child' appearance='default' id="events">
          <Nav>
            <Nav.Item><Button color='green' appearance='primary' className="event" data-duration="1" platform="Online">Konzultáció</Button></Nav.Item>
            <Nav.Item><Button color='green' appearance='primary' className="event" data-duration="1.5" platform="Online">Coach ülés</Button></Nav.Item>
            <Nav.Item><Button color='green' appearance='primary' className="event" data-duration="4" platform="Online">Tréning</Button></Nav.Item>
          </Nav>
      </Navbar>
      <div className='demo-app'>
        <div className='demo-app-main'>
          <FullCalendar
            plugins={[dayGridPlugin, interactionPlugin, timeGridPlugin]}
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'dayGridMonth,timeGridWeek,timeGridDay'
            }}
            initialView='timeGridWeek'
            slotMinTime='08:00:00'
            slotMaxTime='20:00:00'
            dayMaxEvents={true}
            locale={huLocale}
            editable={true}
            selectable={true} 
            droppable={true}
            eventOverlap={false}
            contentHeight='auto'
            eventDataTransform={eventData => ({ // Transform event data
              id: eventData.id,
              title: eventData.title,
              start: eventData.start, 
              end: eventData.end,
              location: eventData.extendedProps.location,
              platform: eventData.extendedProps.platform,
              occasions: eventData.extendedProps.occasions,
              traintitle: eventData.extendedProps.traintitle,
              lengths: eventData.extendedProps.lengths
            })}
            eventReceive={eventReceive}
            //initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
            //eventContent={renderEventContent} // custom render function
            eventClick={handleEventClick}
            //eventsSet={handleEvents} // called after events are initialized/added/changed/removed
            /* you can update a remote database when these fire:
            eventAdd={function(){}}
            eventChange={function(){}}
            eventRemove={function(){}}
            Hossz -->eventData.end + duration * 60 * 60 * 1000
            */
          />
        </div>
      </div>



      <Modal open={open} size={"sm"} backdrop={editable} onClose={handleClose}>
        <Modal.Header>
          <Modal.Title center>{showEventData.title}</Modal.Title>
        </Modal.Header>
          {showEventData && ( //conditional rendering
          <Modal.Body>
            <Form fluid plaintext={editable} formValue={showExtendedData}>
              <Form.Group controlId="name-9">
                <Form.ControlLabel>Platform</Form.ControlLabel>
                <Form.Control name="platform" />
              </Form.Group>
            </Form>
            <Form fluid plaintext={editable} formValue={showExtendedData}>
              <Form.Group>
                <Form.ControlLabel>Cím/Téma</Form.ControlLabel>
                <Form.Control name="traintitle"/>
                <Form.ControlLabel>Alkalmak száma</Form.ControlLabel>
                <InputNumber min={1} max={8} defaultValue={showExtendedData.occasions} plaintext={editable} onChange={createDates} name="occasions"/>
                <Form.ControlLabel>Helyszín</Form.ControlLabel>
                <Form.Control name="location" />
                <Form.ControlLabel>Hossz</Form.ControlLabel>
                <SelectPicker plaintext={editable} data={timelength} defaultValue={String(showExtendedData.lengths)} searchable={false} placeholder={showExtendedData.lengths} name="length" block/>
                {Array.from({ length: numDatePickers }).map((_, index) => (
                  <>
                    <Form.ControlLabel>{`${index + 2}. Alkalom`}</Form.ControlLabel>
                    <DatePicker plaintext={editable} key={index} block></DatePicker>
                  </>
                ))}
              </Form.Group>
            </Form>
            <br></br>
            <p>
              {showEventData.start && showEventData.start.toLocaleString('hu-HU', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric', 
                hour: 'numeric', 
                minute: 'numeric' 
              })}
              {showEventData.end && ' - ' + showEventData.end.toLocaleString('hu-HU', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric', 
                hour: 'numeric', 
                minute: 'numeric' 
              })}
            </p>
          </Modal.Body>
          )}
        <Modal.Footer>
          <Button onClick={handleClose} appearance="primary">{buttonTexts[0]}</Button>
          <Button onClick={handleClose} appearance="subtle">{buttonTexts[1]}</Button>
        </Modal.Footer>
      </Modal>
      
    </CustomProvider>

  )
}

function calculateEndTime(duration) {
  const now = new Date();
  const endTime = new Date(now.getTime() + duration * 60 * 60 * 1000); // Calculate end time based on duration
  return endTime;
}



