import React, { useState, useEffect } from 'react'
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid';
import huLocale from '@fullcalendar/core/locales/hu';
import interactionPlugin, { Draggable } from '@fullcalendar/interaction'
import { createEventId } from './event-utils'; // Import createEventId function

import 'rsuite/dist/rsuite.min.css';
import { Button, CustomProvider } from 'rsuite';
//DatePicker -->Nem heti ismétlődő események -
//-> renderEvents
//Modal/Drawer title alapján máshogy renderelődik, de fixen az egész event-et megkapja
//Overlapping eventek kezelése?
//https://rsuitejs.com/guide/introduction/#license


export default function DemoApp() {
  useEffect(() => {
    const containerEl = document.querySelector("#events");
    let draggable = new Draggable(containerEl, {
      itemSelector: ".event",
      eventData: (eventEl) => {
        const duration = parseFloat(eventEl.getAttribute('data-duration')); // Get duration from data attribute
        const end = calculateEndTime(duration);
        const platform = eventEl.getAttribute('platform');
        const helyszín = "Ház";
        return {
          id: createEventId(),
          title: eventEl.innerText,
          platform : platform,
          duration: duration + ":00",
          start: new Date(),
          location: helyszín,
          end: end 
        };
      }
    });
    return () => draggable.destroy();
  }, []);

  //const [currentEvents, setCurrentEvents] = useState([])
  const INITIAL_EVENTS = [
    {
      title: "Esemény 0",
      platform: "Online",
      duration: "02:00",
      date: new Date()
    }
  ];

  function handleEventClick(clickInfo) {
      console.log(clickInfo.event.extendedProps);
      //clickInfo.event.remove()
  }

  

  return (
    <div className='demo-app'>
      <Sidebar/>
      <div className='demo-app-main'>
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin, timeGridPlugin]}
          headerToolbar={{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          initialView='timeGridWeek'
          locale={huLocale}
          editable={true}
          selectable={true} 
          droppable={true}
          eventOverlap={false}
          eventDataTransform={eventData => ({ // Transform event data
            id: eventData.id,
            title: eventData.title,
            start: eventData.start, 
            end: eventData.end,
            location: eventData.extendedProps.location,
            platform: eventData.extendedProps.platform
          })}
          dayMaxEvents={true}
          eventReceive={eventReceive}
          //initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
          //eventContent={renderEventContent} // custom render function
          eventClick={handleEventClick}
          //eventsSet={handleEvents} // called after events are initialized/added/changed/removed
          /* you can update a remote database when these fire:
          eventAdd={function(){}}
          eventChange={function(){}}
          eventRemove={function(){}}
          */
        />
      </div>
    </div>
  )
}

function calculateEndTime(duration) {
  const now = new Date();
  const endTime = new Date(now.getTime() + duration * 60 * 60 * 1000); // Calculate end time based on duration
  return endTime;
}

function eventReceive(info){
  console.log(info.event.extendedProps.platform);
  window.alert("Időzítésileg itt lenne az extra datok megadása...");
  const helyszín = "Pesti ház";
  info.event.setExtendedProp("location", helyszín);
  return {};
};

function eventChange(changeInfo){
  //console.log(changeInfo.event.extendedProps.platform);
  //window.alert("Időzítésileg itt lenne az extra datok megadása...");
  //const helyszín = "Pesti ház";
  //info.location = helyszín;
  return {
  };
};

function Sidebar() {
  return (
    <div className='demo-app-sidebar'>
      <div className='demo-app-sidebar-section'>
        <h2>Kiadható Események</h2>
        <ul id="events">
          <li className="event" data-duration="1" platform="Online"><Button>Konzultáció</Button></li>
          <li className="event" data-duration="2">Coach ülés</li>
          <li className="event" data-duration="4">Tréning</li>
        </ul>
      </div>

    </div>
  )
}
