import React, { useState, useEffect } from 'react'
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid';
import huLocale from '@fullcalendar/core/locales/hu';
import interactionPlugin, { Draggable } from '@fullcalendar/interaction'
import { createEventId } from './event-utils'; // Import createEventId function
import 'rsuite/dist/rsuite.min.css';
//import '../index.css';
import { Button, CustomProvider, Modal, Container, Sidebar} from 'rsuite';


//DatePicker -->Nem heti ismétlődő események -
//-> renderEvents
//Modal/Drawer title alapján máshogy renderelődik, de fixen az egész event-et megkapja
//Overlapping eventek kezelése?
//https://rsuitejs.com/guide/introduction/#license

//Form -->Plaintext - input váltás (példa alapján)

export default function DemoApp() {
  const [open, setOpen] = React.useState(false);
  const [showEventData, setshowEventData] = React.useState([]);
  const [showExtendedData, setshowExtendedData] = React.useState([]);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


  useEffect(() => {
    const containerEl = document.querySelector("#events");
    let draggable = new Draggable(containerEl, {
      itemSelector: ".event",
      eventData: (eventEl) => {
        const duration = parseFloat(eventEl.getAttribute('data-duration')); // Get duration from data attribute
        const end = calculateEndTime(duration);
        const platform = eventEl.getAttribute('platform');
        const helyszín = "Ház";
        return {
          id: createEventId(),
          title: eventEl.innerText,
          platform : platform,
          duration: duration + ":00",
          start: new Date(),
          location: helyszín,
          end: end 
        };
      }
    });
    return () => draggable.destroy();
  }, []);

  //const [currentEvents, setCurrentEvents] = useState([])
  const INITIAL_EVENTS = [
    {
      title: "Esemény 0",
      platform: "Online",
      duration: "02:00",
      date: new Date()
    }
  ];

  function handleEventClick(clickInfo) {
      console.log(clickInfo.event.start);
      setshowExtendedData(clickInfo.event.extendedProps);
      setshowEventData(clickInfo.event);
      handleOpen();
  }

  function eventReceive(info){
    //window.alert("Időzítésileg itt lenne az extra datok megadása...");
    const helyszín = "Pesti ház";
    info.event.setExtendedProp("location", helyszín);
    setshowExtendedData(info.event.extendedProps);
    setshowEventData(info.event);
    handleOpen();
    return {};
  };
  

  return (
    <CustomProvider>
      <div>
        <div>
          <FullCalendar
            plugins={[dayGridPlugin, interactionPlugin, timeGridPlugin]}
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'dayGridMonth,timeGridWeek,timeGridDay'
            }}
            initialView='timeGridWeek'
            locale={huLocale}
            editable={true}
            selectable={true} 
            droppable={true}
            eventOverlap={false}
            contentHeight={'auto'}
            eventDataTransform={eventData => ({ // Transform event data
              id: eventData.id,
              title: eventData.title,
              start: eventData.start, 
              end: eventData.end,
              location: eventData.extendedProps.location,
              platform: eventData.extendedProps.platform
            })}
            dayMaxEvents={true}
            eventReceive={eventReceive}
            //initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
            //eventContent={renderEventContent} // custom render function
            eventClick={handleEventClick}
            //eventsSet={handleEvents} // called after events are initialized/added/changed/removed
            /* you can update a remote database when these fire:
            eventAdd={function(){}}
            eventChange={function(){}}
            eventRemove={function(){}}
            */
          />
          <Sidebar
            style={{ display: 'flex', flexDirection: 'column' }}
            collapsible>
            <Container id="events" style={{marginTop: 50}}>
              <Button color='green' appearance='primary' className="event" data-duration="1" platform="Online">Konzultáció</Button>
              <br></br>
              <Button color='green' appearance='primary' className="event" data-duration="2" platform="Online">Coach ülés</Button>
              <br></br>
              <Button color='green' appearance='primary' className="event" data-duration="" platform="Online">Tréning</Button>
            </Container>
          </Sidebar>
        </div>
      </div>



      <Modal open={open} size={"lg"} onClose={handleClose}>
        <Modal.Header>
          <Modal.Title>{showEventData.title}</Modal.Title>
        </Modal.Header>
          {showEventData && ( //conditional rendering
          <Modal.Body>
            <p>Helyszín: {showExtendedData.location}</p>
            <p>{showExtendedData.platform}</p>
            {showEventData.start && ( //conditional rendering
            <p>{showEventData.start.toLocaleString('hu-HU', 
            { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric' })} - </p>
            )}
            {showEventData.end && ( //conditional rendering
            <p>{showEventData.end.toLocaleString('hu-HU', 
            { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric' })}</p>
            )}
          </Modal.Body>
          )}
        <Modal.Footer>
          <Button onClick={handleClose} appearance="primary">Ok</Button>
          <Button onClick={handleClose} appearance="subtle">Cancel</Button>
        </Modal.Footer>
      </Modal>
      
    </CustomProvider>

  )
}

function calculateEndTime(duration) {
  const now = new Date();
  const endTime = new Date(now.getTime() + duration * 60 * 60 * 1000); // Calculate end time based on duration
  return endTime;
}



function eventChange(changeInfo){
  //console.log(changeInfo.event.extendedProps.platform);
  //window.alert("Időzítésileg itt lenne az extra datok megadása...");
  //const helyszín = "Pesti ház";
  //info.location = helyszín;
  return {
  };
};

